$(document).ready(function () {

    $(document).foundation();

    var fullPageInit = function () {
        if (Foundation.utils.is_small_only()) {
            // Remove full page
            if ($('.fp-enabled').length) {
                // Destroy all
                $.fn.fullpage.destroy('all');
            }

        } else {

            if ($('.fp-enabled').length < 1) {
                $('#fullpage').fullpage({
                    anchors: ['home','about',
                        'benefits', 'chat'],

                    afterLoad: function (anchorLink, index) {
                        var loadedSection = $(this);

                        $("#menu li").removeClass("active");
                        switch (anchorLink) {
                            case "home":
                                $("#menu li[data-menuanchor=home]").addClass("active");
                                break;
                            case "about":
                                $("#menu li[data-menuanchor=about]").addClass("active");
                                break;
                            case "benefits":
                                $("#menu li[data-menuanchor=benefits]").addClass("active");
                                break;
                            case "chat":
                                $("#menu li[data-menuanchor=chat]").addClass("active");
                                break;
                        }
                    }
                });
            }
        }
    };


    $(window).resize(fullPageInit);
    // Call once on initial load
    fullPageInit();


//roboCareChat

$('#roboCareChat')
  .on('valid.fndtn.abide', function (ev) {
      ev.preventDefault();
      var message = $('*[name=message]').val();
      var form = $(this);
      var data = form.serialize();
      $('*[name=message]').prop("disabled", true);
      $('*[name=message]').val("");
      $(".chat-content").append('<div class="small-6 small-offset-6 columns person-answer"><p>'+message+'</p></div>');
      $(".chat-content").append('<div class="small-6 columns end boot-answer" loader><img src="./images/loader.gif" width="100" /></div>');
                    
                   
                    
                    setTimeout(
              function(){
      $.ajax({
          url : form.attr("action"),
          method : 'POST',
          data : data,
      }).done(function(response){
          $(".chat-content *[loader]").remove();
         $('*[name=message]').prop("disabled", false);
      $(".chat-content").append('<div class="small-6 columns end boot-answer" ><p>'+response.response+'</p></div>');
          console.log(response);
      });     
              }   , 1000           
              
              );
              
                    
  });

});
$('div[data-slick]').slick({
    arrows: true,
    infinite: true,
    autoplay: true
});