$(document).ready(function () {

    $(document).foundation();

    var fullPageInit = function () {
        if (Foundation.utils.is_small_only()) {
            // Remove full page
            if ($('.fp-enabled').length) {
                // Destroy all
                $.fn.fullpage.destroy('all');
            }

        } else {

            if ($('.fp-enabled').length < 1) {
                $('#fullpage').fullpage({
                    anchors: ['home','about',
                        'benefits', 'chat'],

                    afterLoad: function (anchorLink, index) {
                        var loadedSection = $(this);

                        $("#menu li").removeClass("active");
                        switch (anchorLink) {
                            case "home":
                                $("#menu li[data-menuanchor=home]").addClass("active");
                                break;
                            case "about":
                                $("#menu li[data-menuanchor=about]").addClass("active");
                                break;
                            case "benefits":
                                $("#menu li[data-menuanchor=benefits]").addClass("active");
                                break;
                            case "chat":
                                $("#menu li[data-menuanchor=chat]").addClass("active");
                                break;
                        }
                    }
                });
            }
        }
    };


    $(window).resize(fullPageInit);
    // Call once on initial load
    fullPageInit();

});
$('div[data-slick]').slick({
    arrows: true,
    infinite: true,
    autoplay: true
});